const body=document.querySelector("body"),navbar=document.querySelector(".navbar"),menuBtn=document.querySelector(".menu-btn"),cancelBtn=document.querySelector(".cancel-btn"),logo=document.getElementById("blr_logo");menuBtn.onclick=()=>{navbar.classList.add("show"),menuBtn.classList.add("hide"),body.classList.add("disabled")},cancelBtn.onclick=()=>{body.classList.remove("disabled"),navbar.classList.remove("show"),menuBtn.classList.remove("hide")},window.onscroll=()=>{this.scrollY>20?(navbar.classList.add("sticky"),logo.src="img/broo11.png",menuBtn.style.color="white"):(navbar.classList.remove("sticky"),logo.src="img/broo12.png",menuBtn.style.color="black")};